import React from 'react';
import styled from 'styled-components';

const Left = () => {
    return (
        <Style>
            <header>
                <button> <i class="fas fa-circle-notch"></i> Start again </button>
            </header>
            <div className="row">
                <div className="question">What's your available capacity?</div>
                <div className="selector">
                    <i class="fas fa-minus"></i>
                    { capacity } servings
                    <i class="fas fa-plus"></i>
                </div>
            </div>
        </Style>
    );
}

export default Left;

const Style = styled.div`
    background: #121E33;
    min-height: 100vh;
    padding: 20px 50px;
    
    header {
        text-align: right;
        color: #f2ede8;

        button {

            i {
                margin-right: 15px;
            }

            border: none;
            background: none;
            color: #f2ede8;
            font-size: 16px;

            &:hover {
                cursor: pointer;
            }

        }
    }
`
