import React from 'react';
import styled from 'styled-components';

const Left = () => {

    const [capacity, setCapacity] = React.useState(300);

    return (
        <Style>
            <header>
                <button> <i class="fas fa-circle-notch"></i> Start again </button>
            </header>
            <div className="row">
                <div className="upper"> 
                    <div className="question">What's your available capacity?</div>
                    <div className="selector">
                        <i class="fas fa-minus"></i>
                        { capacity } servings
                        <i class="fas fa-plus"></i>
                    </div>
                </div>
                <div className="lower">
                    <div className="range-bg">
                        <div className="range-value">
                            <span className="range-slider"></span>
                        </div>
                    </div>
                </div>
            </div>
        </Style>
    );
}

export default Left;

const Style = styled.div`
    background: #121E33;
    min-height: 100vh;
    padding: 20px 50px;
    
    header {
        text-align: right;
        color: #f2ede8;

        button {

            i {
                margin-right: 15px;
            }

            border: none;
            background: none;
            color: #f2ede8;
            font-size: 16px;

            &:hover {
                cursor: pointer;
            }

        }
    }

    .row {
        margin-top: 50px;
        color: #fff;

        .upper {
            display: flex;
            justify-content: space-between;
            margin-bottom: 20px;
            align-items: baseline;

            .selector {
                background-color: #1d2b44;
                padding: 12px 0;

                i {
                    margin: 0 25px;
                    opacity: 0.7;

                    &:hover {
                        opacity: 1;
                        cursor: pointer;
                    }
                }
            }
        }

        .lower {

            .range-bg {
                height: 5px;
                background-color: #d8d8d8;
                border-radius: 10px;
                position: relative;
                overflow: hidden;

                .range-value {
                    background-color: #8ac03b;
                    position: absolute;
                    height: 100%;
                    width: 100%;
    
                    .range-slider {
                        height: 20px;
                        width: 20px;
                        background: #8ac03b;
                        position: absolute;
                        transform: translateY(-40%);
                        border-radius: 50%;
                        right: 0;
                        z-index: 1;
                        
                        &:hover {
                            cursor: pointer;
                        }
                    }
                }
            }
            
        }
    }
`
