import React from 'react';
import styled from 'styled-components';

const Left = () => {

    const [capacity, setCapacity] = React.useState(300);

    return (
        <Style>
            <header>
                <button> <i class="fas fa-circle-notch"></i> Start again </button>
            </header>
            <div className="row">
                <div className="upper"> 
                    <div className="question">What's your available capacity?</div>
                    <div className="selector">
                        <i class="fas fa-minus" onClick={ () => { capacity < 50 ? setCapacity(0) : setCapacity(capacity - 50) } } />
                        <span>
                            { capacity } servings
                        </span> 
                        <i class="fas fa-plus" onClick={ () => { capacity > 450 ? setCapacity(500) : setCapacity(capacity + 50) } } />
                    </div>
                </div>
                <div className="lower">
                    <input type="range" min="0" step="10" max="500" value="300" class="slider" value={ capacity } onChange={ e => setCapacity(e.target.value)}/>
                </div>
            </div>
        </Style>
    );
}

export default Left;

const Style = styled.div`
    background: #121E33;
    min-height: 100vh;
    padding: 20px 50px;
    
    header {
        text-align: right;
        color: #f2ede8;

        button {

            i {
                margin-right: 15px;
            }

            border: none;
            background: none;
            color: #f2ede8;
            font-size: 16px;

            &:hover {
                cursor: pointer;
            }

        }
    }

    .row {
        margin-top: 50px;
        color: #fff;

        .upper {
            display: flex;
            justify-content: space-between;
            margin-bottom: 20px;
            align-items: baseline;

            .selector {
                background-color: #1d2b44;
                padding: 12px 0;

                span {
                    width: 90px;
                }
                

                i {
                    margin: 0 25px;
                    opacity: 0.7;

                    &:hover {
                        opacity: 1;
                        cursor: pointer;
                    }
                }
            }
        }

        .lower {


            .slider {
                -webkit-appearance: none; 
                background: #8ac03b; 
                width: 100%; 
                height: 5px;
                border-radius: 10px;

                &::-webkit-slider-thumb { 
                    -webkit-appearance: none; 
                    cursor: pointer; 
                    background: #8ac03b; 
                    width: 25px; 
                    height: 25px;
                    border-radius: 50%; 
                } 
            }           
        }
    }
`
