import React from 'react';
import styled from 'styled-components';
import { useSelector, useDispatch } from 'react-redux';

import { nextSubStage, nextStage } from '../state/actions';

const Right = () => {

    const dispatch = useDispatch();
    const { sub_stage, last_stage } = useSelector(state => state);

    return (
        <Style sub_stage={ sub_stage + 1 }>
            <header>
                <span> Input </span>
                <span> Your Earnings </span>
                <span> Key to start </span>
            </header>
            <main hidden={ sub_stage !== 0 }>
                <h1> Revenue Calculator </h1>
                <p> Find out how much you can grow your reveue by adding meal kits </p>
            </main>
            <main hidden={ sub_stage !== 1 }>
                <h2> Your earnings: </h2>
                <table>
                    <tbody>
                        <tr>
                            <td>Revenue</td>
                            <td>$150k</td>
                            <td>$450k</td>
                            <td>20%</td>
                        </tr>
                        <tr>
                            <td>Net profit</td>
                            <td>-20%</td>
                            <td>10%</td>
                            <td>30%</td>
                        </tr>
                        <tr>
                            <td>Capacity utilization</td>
                            <td>70%</td>
                            <td>80%</td>
                            <td>10%</td>
                        </tr>
                    </tbody>
                </table>
            </main>
            <footer>
                <button hidden={ sub_stage === last_stage } onClick={ () => dispatch(nextSubStage()) }>Next</button>
                <button hidden={ sub_stage !== last_stage } onClick={ () => dispatch(nextStage()) }>Email me Report</button>
            </footer>
        </Style>
    );
}

export default Right;

const Style = styled.div(
    ({ sub_stage }) => `
    background: #fff;
    min-height: 100vh;
    padding: 20px 50px;
    display: flex;
    flex-direction: column;
    justify-content: space-between;

    header {
        display: flex;
        justify-content: space-between;

        span {
            font-size: 14px;
            color: #1d2b44;
            padding: 5px 0;
        
            &:nth-child(${ sub_stage }) {
                font-weight: bold;
                border-bottom: 2px solid #1d2b44;
            }
        }
    }

    main {
        color: #1d2b44;
        
        h1 {
            font-size: 48px;
            margin-bottom: 20px;
            font-weight: normal;
        }

        h2 {
            font-size: 24px;
            font-weight: 500;
        }

        p {
            font-weight: 500;
        }

        table {
            width: 100%;
            
            tr {

                td {
                    padding: 10px 0;
                
                    &:nth-child(1) {
                        color: #1d2b44;
                    }

                    &:nth-child(2) {
                        font-size: 20px;
                        color: #1d2b44;
                        font-weight: 500;
                    }

                    &:nth-child(3) {
                        font-size: 20px;
                        color: #8ac03b;
                        font-weight: 500;
                    }

                    &:nth-child(3) {
                        font-size: 20px;
                        color: #8ac03b;
                        font-weight: 500;
                    }


                }
            }
        }
    }

    footer {
        align-self: flex-end;

        button {
            padding: 13px 50px;
            background: #8ac03b;
            color: #fff;
            text-transform: uppercase;
            border: none;

            &:hover {
                cursor: pointer;
            }
        }
    }


    `
)
